    // set colormap
    Color[] colors = new Color[] { Color.BLUE, Color.GREEN, Color.YELLOW, Color.RED };
    double[] depths = new double[] { 0, 0.5, 1, 1.5 };
    plot.addLegend("DEPTH", Color.BLACK);
