                  "- Cost savings \n";
var keywordFrequency = new Dictionary<string, int>();
var words = Regex.Matches(article, @"\b[\w']*\b")
                .Cast<Match>()
                .Select(match => match.Value.ToLower())
                .ToList();
foreach (var word in words)
