func executeQuery(query: String) -> [[String: Any]]? {
    guard mysql.selectDatabase(named: "database_name") else {
        print(mysql.errorMessage())
        return nil
    }

    guard mysql.query(statement: query) else {
        print(mysql.errorMessage())
        return nil
