How can I use Go to calculate the limit of the function f(x) = (x^3 - 4x^2 - x + 4)/(x^2 - 4x + 3) as x approaches 1 using L'Hpital's rule and including the second derivative of the function in the calculation? Here is the XML data of the function:
<function>
  <numerator>
    <term><coefficient>1</coefficient><variable>x</variable><exponent>3</exponent></term>
    <term><coefficient>-4</coefficient><variable>x</variable><exponent>2</exponent></term>
    <term><coefficient>-1</coefficient><variable>x</variable></term>
    <term><coefficient>4</coefficient></term>
  </numerator>
  <denominator>
    <term><coefficient>1</coefficient><variable>x</variable><exponent>2</exponent></term>
    <term><coefficient>-4</coefficient><variable>x</variable></term>
    <term><coefficient>3</coefficient></term>
  </denominator>
</function>
Here is the Go code:
package main
import (
    "encoding/xml"
    "fmt"
    "math"
)
type Function struct {
    Numerator   []Term `xml:"numerator>term"`
    Denominator []Term `xml:"denominator>term"`
}
type Term struct {
    Coefficient float64 `xml:"coefficient"`
    Variable    string  `xml:"variable"`
    Exponent    float64 `xml:"exponent"`
}
func main() {
    f := Function{}
    xml.Unmarshal([]byte(`<function>
  <numerator>
    <term><coefficient>1</coefficient><variable>x</variable><exponent>3</exponent></term>
    <term><coefficient>-4</coefficient><variable>x</variable><exponent>2</exponent></term>
    <term><coefficient>-1</coefficient><variable>x</variable></term>
    <term><coefficient>4</coefficient></term>
  </numerator>
  <denominator>
    <term><coefficient>1</coefficient><variable>x</variable><exponent>2</exponent></term>
    <term><coefficient>-4</coefficient><variable>x</variable></term>
    <term><coefficient>3</coefficient></term>
  </denominator>
</function>`), &f)
    x := 1.0
    fmt.Println(limit(f, x))
}
func limit(f Function, x float64) float64 {
    numerator := 0.0
    denominator := 0.0
    for _, term := range f.Numerator {
        coefficient := term.Coefficient
        exponent := term.Exponent
        if term.Variable == "x" {
            coefficient *= math.Pow(x, exponent)
            exponent -= 1.0
            if exponent < 0 {
                coefficient = 0.0
            }
        }
        numerator += coefficient * math.Pow(x, exponent)
    }
    for _, term := range f.Denominator {
        coefficient := term.Coefficient
        exponent := term.Exponent
        if term.Variable == "x" {
            coefficient *= math.Pow(x, exponent)
            exponent -= 1.0
            if exponent < 0 {
                coefficient = 0.0
            }
        }
        denominator += coefficient * math.Pow(x, exponent)
    }
    if math.IsNaN(numerator / denominator) {
        return limit(f, x+0.0001)
    }
    if math.IsInf(numerator / denominator, 0) {
        return math.NaN()
    }
    if numerator/denominator != numerator/denominator {
        return math.NaN()
    }
    return numerator / denominator
}

