  vector<double> energy_data = {2.3, 4.5, 3.2, 5.6, 4.1, 2.8, 3.9, 6.5, 4.3, 5.1, 3.7, 4.9, 5.3, 3.6, 2.7, 4.8, 3.1, 5.2, 4.4, 6.1, 3.8, 5.7, 2.9, 4.2, 6.2, 3.5, 5.9, 4.7, 2.6, 3.3, 5.4, 4.6, 3.4, 2.4, 6.3, 4.0, 5.5, 2.5, 6.4, 3.0};

