    let destinations = ["mountains", "beach", "national park", "lake"]

    let activity1 = outdoorActivities.randomElement()!
    var activity2 = outdoorActivities.randomElement()!
    while activity2 == activity1 {
        activity2 = outdoorActivities.randomElement()!
    }

    var activity3 = outdoorActivities.randomElement()!
    while activity3 == activity1 || activity3 == activity2 {
        activity3 = outdoorActivities.randomElement()!
    }

    let destination = destinations.randomElement()!
    let priceRange = ["$500", "$1000", "$1500"]
