}

function sortTabs() {
 chrome.tabs.query({ currentWindow: true }, (tabs) => {
 const tabPriorities = tabs.map((tab) => {
 return new Promise((resolve) => {
 chrome.storage.sync.get([`tab-${tab.id}`], (result) => {
 const priorityData = result[`tab-${tab.id}`] || {
 level: null,
 timestamp: null,
 };
 resolve({ tabId: tab.id, ...priorityData });
 });
 });
 });

 Promise.all(tabPriorities).then((priorities) => {
 reorderTabs(priorities);
 });
 });
}

function reorderTabs(tabPriorities) {
 tabPriorities.sort((a, b) => {
 if (a.level === null && b.level !== null) return 1;
 if (a.level !== null && b.level === null) return -1;
 if (a.level === null && b.level === null) return 0;

 if (a.level !== b.level) {
 return a.level - b.level;
 } else {
 return a.timestamp - b.timestamp;
 }
 });

 for (let i = 0; i < tabPriorities.length; i++) {
 chrome.tabs.move(tabPriorities[i].tabId, { index: i });
 }

 chrome.tabs.update(tabPriorities[0].tabId, { active: true });
}

