 const insights = {};

 // 2.1. Helper function to get the insight value
 function getInsightValue(dataInsights, metricName) {
 // Find the insight object that matches the metricName
 const insight = dataInsights.find((i) => i.name === metricName);
 // If the insight object is found, return the value of the first value object in the values array
 // Otherwise, return 0
 return insight?.values[0]?.value || 0;
 }

 // 2.2. Iterate through the INSIGHTS\_METRICS\_MAP and calculate metric values
 for (const [metric, value] of Object.entries(INSIGHTS\_METRICS\_MAP)) {
