//Setup
function setup() {
  var characters = document.createElement("div");
  characters.setAttribute("id", "characters");
  var john = document.createElement("div");
  john.setAttribute("class", "character");
  john.innerHTML = "John - Programmer";
  var jane = document.createElement("div");
  jane.setAttribute("class", "character");
  jane.innerHTML = "Jane - Designer";
  characters.appendChild(john);
  characters.appendChild(jane);
  var setting = document.createElement("div");
  setting.setAttribute("id", "setting");
  setting.innerHTML = "Location: San Francisco, Time: Morning";
  var scene = document.getElementById("scene");
  scene.appendChild(characters);
  scene.appendChild(setting);
}
//Confrontation
function confrontation() {
  var problem = document.createElement("div");
  problem.setAttribute("id", "problem");
  problem.innerHTML = "Problem: Code not working";
  var attempt1 = document.createElement("div");
  attempt1.setAttribute("class", "attempt");
  attempt1.innerHTML = "John is trying to debug the code";
  var attempt2 = document.createElement("div");
  attempt2.setAttribute("class", "attempt");
  attempt2.innerHTML = "Jane is redesigning the UI";
  var scene = document.getElementById("scene");
  scene.appendChild(problem);
  scene.appendChild(attempt1);
  scene.appendChild(attempt2);
}
//Resolution
function resolution() {
  var solution = document.createElement("div");
  solution.setAttribute("id", "solution");
  solution.innerHTML = "Code fixed and design improved";
  var ending1 = document.createElement("div");
  ending1.setAttribute("class", "ending");
  ending1.innerHTML = "John is relieved that the code is working";
  var ending2 = document.createElement("div");
  ending2.setAttribute("class", "ending");
  ending2.innerHTML = "Jane is proud of the new UI design";
  var scene = document.getElementById("scene");
  scene.appendChild(solution);
  scene.appendChild(ending1);
  scene.appendChild(ending2);
}
//Main function
function main() {
  setup();
  confrontation();
  resolution();
}
main();

