public interface IPosition
{
 Vector3 Position { get; }
}

public interface ITargetable : IPosition
{
 float Radius { get; }
 bool IsAlive { get; }
 void DealDamage(int damage) { }
}

public enum MovementType { Ground, Flying }

public interface IMovement
{
 // These stats are public mainly for displaying in the UI. The movement component will move the unit on its own, given a target.

 MovementType Type { get; }
 float TopSpeed { get; } 
 float MaxForce { get; } // Acceleration depends on weight.
 float RotationSpeed { get; } // in °/s
 float MudEfficiency { get; } // Placeholder stat. Perhaps treads can travel through mud and rough terrain faster than wheels.
 bool HasReachedTarget { get; }

 void SetTarget(ITargetable target, float proximity = 0.5f);
}

public interface IWeapon
{
 // Once a target is set, the weapon will automatically attack it. Set the target to null to stop firing.
 void SetTarget(ITargetable target); 
 bool CanFire(ITargetable target);

 float Cooldown { get; }
 float Range { get; }
}

public interface ISensor
{
 IEnumerable GetDetectedUnits();
 bool CanDetect(BattlerUnit unit);
}

public interface IHealth
{
 int Health { get; }
 int MaxHealth { get; }
 bool IsAlive { get; }
 void DealDamage(int damage);

 public event Action OnDeath;
 public event Action OnDamageTaken;
}

public enum Team { Player, Enemy, Neutral }

// A fixed location target (as opposed to targeting another unit or building)
public struct GroundTarget : ITargetable
{
 public GroundTarget(Vector3 position);
}

public class BattlerUnit : MonoBehaviour, ITargetable, IHealth
{
 public GameObject ExplosionEffect;

 public float Weight;
 public int MaxHealth { get; }
 public int Health { get; }
 public Team Team;

 public IWeapon[] Weapons;
 public IMovement Movement; // Only one movement component. 
 public ISensor[] Sensors;
 public UnitBehaviour[] Behaviours;

 public Vector3 Position { get; }
 public float Radius { get; }
 public bool IsAlive { get; }

 public void DealDamage(int damage);
}

public class UnitManager : MonoBehaviour
{
 public static UnitManager Instance { get; }

 // Makes the UnitManager aware of the unit.
 public void Register(BattlerUnit unit);
 public void Unregister(BattlerUnit unit);

 // Methods to find units
 public IEnumerable FindInCircle(Vector3 center, float radius);
 public IEnumerable FindInArc(Vector3 center, float radius, Vector3 forward, float arcDegrees);
 public BattlerUnit GetClosest(Vector3 point);
}

